#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define HASH_SIZE 100

typedef struct Pair Pair;

typedef struct HashMap HashMap;

struct Pair {
    char *key;
    int val;
    Pair *next;
};

struct HashMap {
    Pair **buckets;
};

unsigned int hash(const char *key) {
    unsigned int hash_val = 0;

    while (*key != '\0') {
        hash_val = (hash_val << 5) + *key;
        key++;
    }

    return hash_val % HASH_SIZE;
}

HashMap* createHashMap() {
    HashMap *map;

    if ((map = malloc(sizeof(HashMap))) == NULL) {
        perror("Allocate memory error!");
        exit(EXIT_FAILURE);
    }

    map->buckets = malloc(sizeof(Pair*) * HASH_SIZE);

    for (int i = 0; i < HASH_SIZE; i++) {
        map->buckets[i] = NULL;
    }

    return map;
}

void insert(const HashMap* map, const char* key, const int val) {
    const unsigned int index = hash(key);

    Pair *cur = map->buckets[index];

    while (cur != NULL) {
        if (strcmp(cur->key, key) == 0) {
            cur->val = val;
            return;
        }
        cur = cur->next;
    }

    Pair *pair;

    if ((pair = malloc(sizeof(Pair))) == NULL) {
        perror("Allocate memory error!");
        exit(EXIT_FAILURE);
    }

    pair->key = strdup(key);

    if (pair->key == NULL) {
        perror("Allocate memory error!");
        free(pair);
        exit(EXIT_FAILURE);
    }

    pair->val = val;
    pair->next = map->buckets[index];

    map->buckets[index] = pair;
}

void delete(const HashMap* map, const char* key) {
    const unsigned int index = hash(key);

    Pair *cur = map->buckets[index];
    const Pair *root = cur;
    Pair *pre = cur;

    while (cur != NULL) {
        if (strcmp(cur->key, key) == 0) {
            Pair *tmp = cur;

            if (cur == root) {
                map->buckets[index] = cur->next;
            } else {
                pre->next = cur->next;
            }

            free(tmp->key);
            free(tmp);
            break;
        }

        pre = cur;
        cur = cur->next;
    }
}

int search(const HashMap* map, const char* key) {
    const unsigned int index = hash(key);

    const Pair *cur = map->buckets[index];

    if (cur == NULL) {
        return 0;
    }

    while (cur != NULL) {
        if (strcmp(cur->key, key) == 0) {
            return cur->val;
        }

        cur = cur->next;
    }

    return 0;
}

void freeHashMap(HashMap* map) {
    for (int i = 0; i < HASH_SIZE; i++) {
        Pair *cur = map->buckets[i];

        while (cur != NULL) {
            Pair *tmp = cur;
            cur = cur->next;

            free(tmp->key);
            free(tmp);
        }
    }

    free(map->buckets);
    free(map);
}

int* findSubstring(char* s, char** words, int wordsSize, int* returnSize) {
    int len = strlen(s);

    int *nums = malloc(sizeof(int) * len);

    HashMap *wordMap = createHashMap();

    for (int i = 0; i < wordsSize; i++) {
        insert(wordMap, words[i], search(wordMap, words[i]) + 1);
    }

    int wl = strlen(words[0]);

    *returnSize = 0;

    for (int i = 0; i <= len - wordsSize * wl; i++) {
        char *ts = malloc(sizeof(char) * (wl + 1));

        HashMap *temp = NULL;
        int count = 0;
        for (int j = 0; j < wordsSize; j++) {
            memcpy(ts, s + i + j * wl, wl);
            ts[wl] = '\0';

            if (!search(wordMap, ts)) {
                break;
            }

            if (temp == NULL) {
                temp = createHashMap();
            }

            int tc = search(temp, ts);

            if (tc + 1 > search(wordMap, ts)) {
                break;
            }
            count++;
            insert(temp, ts, tc + 1);
        }

        if (count == wordsSize) {
            nums[*returnSize] = i;
            (*returnSize)++;
        }

        if (temp != NULL) {
            freeHashMap(temp);
        }
        free(ts);
    }

    freeHashMap(wordMap);

    return nums;
}

